    <tr>
      <th scope=\"col\">ID</th>
      <th scope=\"col\">Nom</th>
      <th scope=\"col\">Actions</th>
    </tr>
  </thead>
  <tbody>
  {% for categorie in categories %}
    <tr>
      <td>{{categorie.id}}</td>
      <td>{{categorie.name}}</td>
     
      <td class=\"text-center\"><a class=\"btn btn-secondary\" href=\"{{path(\"admin_categories_modifier\", {id: categorie.id})}}\" role=\"button\">Modifier</a>
</td>
      
    </tr>
  {% endfor %}
  </tbody>
</table>
{% endblock %}", "admin/categories/index.html.twig", "C:\\Users\\totoy91\\MKprojet\\templates\\admin\\categories\\index.html.twig");
